mapper.select:

select *
from (select @ROWNUM:=@ROWNUM+1 as rn,bno,rno,reply,replyer,replyDate,updatedate
    from pr_reply,(SELECT @ROWNUM :=0) AS t
    where bno=#{bno}
    	and @ROWNUM< #{cri.pageNum} * #{cri.amount}
    order by rno
    )B
where rn>(#{cri.pageNum}-1) * #{cri.amount}

select count(rno)
from pr_reply
where bno=#{bno}
