mapper.select:

select *
from (select @ROWNUM:=@ROWNUM+1 as rn ,bno,cgo,title,content,writer,regdate,updatedate,replycnt
      from tbl_board,(SELECT @ROWNUM :=0) AS r
      where 
 

	
	
 
      @ROWNUM < #{pageNum} * #{amount}
      order by bno desc		      
      )A
where rn > (#{pageNum}-1) * #{amount}


select *
from (select @ROWNUM:=@ROWNUM+1 as rn ,bno,cgo,title,content,writer,regdate,updatedate,replycnt
      from tbl_board,(SELECT @ROWNUM :=0) AS r
      where cgo='자유게시판' and
 

	
	
 
      @ROWNUM < #{pageNum} * #{amount}
      order by bno desc		      
      )A
where rn > (#{pageNum}-1) * #{amount}


